package mul_sheet;

public class mul_99 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		int m=1;
     for(;n<=9;n++)
     {
    	 for(;m<=n;m++)
    	 {
    		 System.out.printf("%d*%d=%d\t", m,n,n*m);
    	 }
    	 System.out.printf("\n");
    	 m=1;
     }
	}

}
